package operadorCondicional;

public class Prinfejemplo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 15;
		float f1 = 3.5f;
		String nom1 = "Mario";
		char inicial = 'S';
		
		System.out.printf("El valor del número entero es: %d\n", num1);
		System.out.printf("El valor del número decimal es: %.2f\n", f1);
		System.out.printf("El valor de la cadena es: %s\n", nom1);
		System.out.printf("El valor del caracter es: %c\n", inicial);
		System.out.printf("El entero es: %d y el decimal es: %.2f\n",num1 ,f1);
		System.out.printf("La cadena es: %s y la inicial es: %c\n", nom1, inicial);
		System.out.printf("Los valores son: %d, %f, %s, %c\n", num1, f1, nom1, inicial);
	
	}

}
